import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

const API_URL = 'http://localhost:5001/api';

const TIME_RANGES = {
  'Last 4 weeks': 'short_term',
  'Last 6 months': 'medium_term',
  'All time': 'long_term'
};

const formatDuration = (minutes) => {
  if (!minutes) return '0m';
  const hrs = Math.floor(minutes / 60);
  const mins = Math.round(minutes % 60);
  return hrs > 0 ? `${hrs}h ${mins}m` : `${mins}m`;
};

const Home = () => {
  const [topTracks, setTopTracks] = useState([]);
  const [topArtists, setTopArtists] = useState([]);
  const [topAlbums, setTopAlbums] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [timeRange, setTimeRange] = useState('short_term');
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const handleSpotifyCallback = async (code) => {
      try {
        const response = await axios.get(`${API_URL}/auth/callback`, {
          params: { code }
        });
        const { access_token, userId } = response.data;

        localStorage.setItem('spotify_access_token', access_token);
        localStorage.setItem('spotify_user_id', userId);
        await fetchData(access_token, userId);

        navigate('/', { replace: true });
      } catch (err) {
        setError('Failed to authenticate with Spotify');
        setLoading(false);
      }
    };

    const params = new URLSearchParams(location.search);
    const code = params.get('code');

    if (code) {
      handleSpotifyCallback(code);
    } else {
      const savedToken = localStorage.getItem('spotify_access_token');
      const userId = localStorage.getItem('spotify_user_id');
      if (savedToken && userId) {
        fetchData(savedToken, userId);
      } else {
        handleLogin();
      }
    }
  }, [location, navigate]);

  useEffect(() => {
    const savedToken = localStorage.getItem('spotify_access_token');
    const userId = localStorage.getItem('spotify_user_id');
    if (savedToken && userId) {
      fetchData(savedToken, userId);
    }
  }, [timeRange]);

  const handleLogin = async () => {
    try {
      const response = await axios.get(`${API_URL}/auth/login`);
      window.location.href = response.data.url;
    } catch (err) {
      setError('Failed to initialize Spotify login');
      setLoading(false);
    }
  };

  const fetchData = async (token, userId) => {
    try {
      setLoading(true);
      const [tracksRes, artistsRes, albumsRes] = await Promise.all([
        axios.get(`${API_URL}/auth/top-tracks`, {
          params: { access_token: token, userId, time_range: timeRange }
        }),
        axios.get(`${API_URL}/auth/top-artists`, {
          params: { access_token: token, userId, time_range: timeRange }
        }),
        axios.get(`${API_URL}/auth/top-albums`, {
          params: { access_token: token, userId, time_range: timeRange }
        })
      ]);

      setTopTracks(tracksRes.data.items);
      setTopArtists(artistsRes.data.items);
      setTopAlbums(albumsRes.data.items);
      setLoading(false);
    } catch (err) {
      if (err.response?.status === 401) {
        localStorage.removeItem('spotify_access_token');
        localStorage.removeItem('spotify_user_id');
        handleLogin();
      } else {
        setError('Failed to fetch data');
        setLoading(false);
      }
    }
  };

  const renderTimeRangeSelector = () => (
    <select 
      className="bg-gray-800 text-white px-4 py-2 rounded-lg"
      value={timeRange}
      onChange={(e) => setTimeRange(e.target.value)}
    >
      {Object.entries(TIME_RANGES).map(([label, value]) => (
        <option key={value} value={value}>{label}</option>
      ))}
    </select>
  );

  const renderSection = (title, items, renderItem) => (
    <div className="mb-12">
      <div className="flex justify-between items-center mb-6">
        <div>
          <h2 className="text-3xl font-bold">{title}</h2>
          <p className="text-gray-400">Your top {title.toLowerCase()} from the {
            Object.entries(TIME_RANGES).find(([_, value]) => value === timeRange)[0].toLowerCase()
          }</p>
        </div>
        <div className="flex gap-4 items-center">
          {renderTimeRangeSelector()}
          <button className="p-2 hover:bg-gray-800 rounded-full">
            <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
              <path d="M5 3a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2V5a2 2 0 00-2-2H5zM5 11a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2v-2a2 2 0 00-2-2H5zM11 5a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2V5zM11 13a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2v-2z" />
            </svg>
          </button>
          <button className="p-2 hover:bg-gray-800 rounded-full">
            <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z" clipRule="evenodd" />
            </svg>
          </button>
          <button className="p-2 hover:bg-gray-800 rounded-full">
            <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M7.293 14.707a1 1 0 010-1.414L10.586 10 7.293 6.707a1 1 0 011.414-1.414l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414 0z" clipRule="evenodd" />
            </svg>
          </button>
          <button className="p-2 hover:bg-gray-800 rounded-full">
            <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
              <path d="M10 6a2 2 0 110-4 2 2 0 010 4zM10 12a2 2 0 110-4 2 2 0 010 4zM10 18a2 2 0 110-4 2 2 0 010 4z" />
            </svg>
          </button>
        </div>
      </div>

      <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-7 gap-4">
        {items.slice(0, 50).map(renderItem)}
      </div>
    </div>
  );

  const renderTrack = (track, index) => (
    <div key={track.id} className="bg-gray-800/30 p-4 rounded-lg group hover:bg-gray-800/60 transition-all">
      <div className="relative mb-4">
        <img 
          src={track.album?.images[0]?.url} 
          alt={track.name}
          className="w-full aspect-square object-cover rounded-md"
        />
        <div className="absolute inset-0 bg-black/40 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
          <button className="p-3 bg-green-500 rounded-full">
            <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM9.555 7.168A1 1 0 008 8v4a1 1 0 001.555.832l3-2a1 1 0 000-1.664l-3-2z" clipRule="evenodd" />
            </svg>
          </button>
        </div>
      </div>
      <div className="space-y-1">
        <p className="font-bold truncate">{index + 1}. {track.name}</p>
        <p className="text-gray-400 text-sm truncate">{track.artists.map(a => a.name).join(', ')}</p>
        <div className="text-gray-500 text-sm">
          {track.stats?.playCount} streams • {Math.round(track.duration_ms / 60000)} minutes
        </div>
      </div>
    </div>
  );

  const renderArtist = (artist, index) => (
    <div key={artist.id} className="bg-gray-800/30 p-4 rounded-lg group hover:bg-gray-800/60 transition-all">
      <div className="relative mb-4">
        <img 
          src={artist.images[0]?.url} 
          alt={artist.name}
          className="w-full aspect-square object-cover rounded-full"
        />
        <div className="absolute inset-0 bg-black/40 opacity-0 group-hover:opacity-100 transition-opacity rounded-full flex items-center justify-center">
          <button className="p-3 bg-green-500 rounded-full">
            <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM9.555 7.168A1 1 0 008 8v4a1 1 0 001.555.832l3-2a1 1 0 000-1.664l-3-2z" clipRule="evenodd" />
            </svg>
          </button>
        </div>
      </div>
      <div className="space-y-1 text-center">
        <p className="font-bold truncate">{index + 1}. {artist.name}</p>
        <div className="text-gray-500 text-sm">
          {artist.stats?.playCount} minutes • {artist.stats?.trackCount} streams
        </div>
      </div>
    </div>
  );

  const renderAlbum = (album, index) => (
    <div key={album.id} className="bg-gray-800/30 p-4 rounded-lg group hover:bg-gray-800/60 transition-all">
      <div className="relative mb-4">
        <img 
          src={album.images[0]?.url} 
          alt={album.name}
          className="w-full aspect-square object-cover rounded-md"
        />
        <div className="absolute inset-0 bg-black/40 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
          <button className="p-3 bg-green-500 rounded-full">
            <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM9.555 7.168A1 1 0 008 8v4a1 1 0 001.555.832l3-2a1 1 0 000-1.664l-3-2z" clipRule="evenodd" />
            </svg>
          </button>
        </div>
      </div>
      <div className="space-y-1">
        <p className="font-bold truncate">{index + 1}. {album.name}</p>
        <p className="text-gray-400 text-sm truncate">{album.artists.map(a => a.name).join(', ')}</p>
        <div className="text-gray-500 text-sm">
          {album.stats?.playCount} streams • {Math.round(album.stats?.totalMinutes)} minutes
        </div>
      </div>
    </div>
  );

  if (loading) return (
    <div className="flex justify-center items-center min-h-screen">
      <div className="text-xl">Loading your music profile...</div>
    </div>
  );

  if (error) return (
    <div className="flex justify-center items-center min-h-screen">
      <div className="text-xl text-red-500">{error}</div>
    </div>
  );

  return (
    <div className="container mx-auto p-4 space-y-8">
      {renderSection('Top tracks', topTracks, renderTrack)}
      {renderSection('Top artists', topArtists, renderArtist)}
      {renderSection('Top albums', topAlbums, renderAlbum)}
    </div>
  );
};

export default Home;
