import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

const API_URL = 'http://localhost:5001/api';

const TIME_RANGES = {
  'Last 4 weeks': 'short_term',
  'Last 6 months': 'medium_term',
  'All time': 'long_term'
};

const formatDuration = (duration) => {
  const minutes = Math.floor(duration / 60000);
  const seconds = Math.floor((duration % 60000) / 1000);
  return `${minutes}:${seconds.toString().padStart(2, '0')}`;
};

const Home = () => {
  const [tracks, setTracks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [viewMode, setViewMode] = useState('recent'); // 'recent' or 'top'
  const [timeRange, setTimeRange] = useState('medium_term');
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const handleSpotifyCallback = async (code) => {
      try {
        const response = await axios.get(`${API_URL}/auth/callback`, {
          params: { code }
        });
        const { access_token } = response.data;

        localStorage.setItem('spotify_access_token', access_token);
        await fetchTracks(access_token);

        navigate('/', { replace: true });
      } catch (err) {
        setError('Failed to authenticate with Spotify');
        setLoading(false);
      }
    };

    const params = new URLSearchParams(location.search);
    const code = params.get('code');

    if (code) {
      handleSpotifyCallback(code);
    } else {
      const savedToken = localStorage.getItem('spotify_access_token');
      if (savedToken) {
        fetchTracks(savedToken);
      } else {
        handleLogin();
      }
    }
  }, [location, navigate]);

  useEffect(() => {
    const savedToken = localStorage.getItem('spotify_access_token');
    if (savedToken) {
      fetchTracks(savedToken);
    }
  }, [viewMode, timeRange]);

  const handleLogin = async () => {
    try {
      const response = await axios.get(`${API_URL}/auth/login`);
      window.location.href = response.data.url;
    } catch (err) {
      setError('Failed to initialize Spotify login');
      setLoading(false);
    }
  };

  const fetchTracks = async (token) => {
    try {
      setLoading(true);
      const endpoint = viewMode === 'recent' ? 'recent-tracks' : 'top-tracks';
      const params = { access_token: token };

      if (viewMode === 'top') {
        params.time_range = timeRange;
      }

      const response = await axios.get(`${API_URL}/auth/${endpoint}`, { params });
      const items = viewMode === 'recent' ? 
        response.data.items.map(item => item.track) : 
        response.data.items;

      setTracks(items || []);
      setLoading(false);
    } catch (err) {
      if (err.response?.status === 401) {
        localStorage.removeItem('spotify_access_token');
        handleLogin();
      } else {
        setError('Failed to fetch tracks');
        setLoading(false);
      }
    }
  };

  if (loading) return (
    <div className="flex justify-center items-center min-h-screen">
      <div className="text-xl">Loading your music profile...</div>
    </div>
  );

  if (error) return (
    <div className="flex justify-center items-center min-h-screen">
      <div className="text-xl text-red-500">{error}</div>
    </div>
  );

  return (
    <div className="container mx-auto p-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">
          {viewMode === 'recent' ? 'Recently Played' : 'Top Tracks'}
        </h1>
        <div className="flex gap-4">
          <select 
            className="bg-gray-800 text-white px-4 py-2 rounded-lg"
            value={viewMode}
            onChange={(e) => setViewMode(e.target.value)}
          >
            <option value="recent">Recently Played</option>
            <option value="top">Top Tracks</option>
          </select>

          {viewMode === 'top' && (
            <select 
              className="bg-gray-800 text-white px-4 py-2 rounded-lg"
              value={timeRange}
              onChange={(e) => setTimeRange(e.target.value)}
            >
              {Object.entries(TIME_RANGES).map(([label, value]) => (
                <option key={value} value={value}>{label}</option>
              ))}
            </select>
          )}
        </div>
      </div>

      {tracks.length === 0 ? (
        <div className="text-center text-gray-400">No tracks found. Try playing some music on Spotify!</div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {tracks.map((track) => (
            <div key={track.id} className="bg-gray-800 p-4 rounded-lg shadow hover:shadow-lg transition-all">
              {track.album?.images?.[0]?.url && (
                <img 
                  src={track.album.images[0].url} 
                  alt={track.name}
                  className="w-full h-48 object-cover rounded-md mb-3"
                />
              )}
              <h2 className="text-xl font-semibold">{track.name}</h2>
              <p className="text-gray-400 mb-2">{track.artists.map(artist => artist.name).join(', ')}</p>

              <div className="flex justify-between items-center text-sm text-gray-400 mb-2">
                <span>Duration: {track.duration_readable || formatDuration(track.duration_ms)}</span>
                {viewMode === 'top' && (
                  <span className="bg-purple-900 px-2 py-1 rounded">
                    {track.recent_plays} recent plays
                  </span>
                )}
                {viewMode === 'recent' && track.played_at && (
                  <span>
                    Played: {new Date(track.played_at).toLocaleString()}
                  </span>
                )}
              </div>

              {track.preview_url && (
                <audio controls className="mt-2 w-full">
                  <source src={track.preview_url} type="audio/mpeg" />
                </audio>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
