import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

const API_URL = 'http://localhost:5001/api';

const Home = () => {
  const [topTracks, setTopTracks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const handleSpotifyCallback = async (code) => {
      try {
        // Exchange code for tokens
        const response = await axios.get(`${API_URL}/auth/callback`, {
          params: { code }
        });
        const { access_token } = response.data;

        // Save token and fetch data
        localStorage.setItem('spotify_access_token', access_token);
        await fetchTopTracks(access_token);

        // Clean up URL
        navigate('/', { replace: true });
      } catch (err) {
        setError('Failed to authenticate with Spotify');
        setLoading(false);
      }
    };

    // Check if we're handling a callback
    const params = new URLSearchParams(location.search);
    const code = params.get('code');

    if (code) {
      handleSpotifyCallback(code);
    } else {
      // Check if we have a token in localStorage
      const savedToken = localStorage.getItem('spotify_access_token');
      if (savedToken) {
        fetchTopTracks(savedToken);
      } else {
        // If no token, we need to login
        handleLogin();
      }
    }
  }, [location, navigate]);

  const handleLogin = async () => {
    try {
      const response = await axios.get(`${API_URL}/auth/login`);
      window.location.href = response.data.url;
    } catch (err) {
      setError('Failed to initialize Spotify login');
      setLoading(false);
    }
  };

  const fetchTopTracks = async (token) => {
    try {
      const response = await axios.get(`${API_URL}/auth/top-tracks`, {
        params: { access_token: token }
      });
      setTopTracks(response.data.items || []);
      setLoading(false);
    } catch (err) {
      if (err.response?.status === 401) {
        // Token expired, clear it and redirect to login
        localStorage.removeItem('spotify_access_token');
        handleLogin();
      } else {
        setError('Failed to fetch top tracks');
        setLoading(false);
      }
    }
  };

  if (loading) return (
    <div className="flex justify-center items-center min-h-screen">
      <div className="text-xl">Loading your music profile...</div>
    </div>
  );

  if (error) return (
    <div className="flex justify-center items-center min-h-screen">
      <div className="text-xl text-red-500">{error}</div>
    </div>
  );

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Your Top Tracks</h1>
      {topTracks.length === 0 ? (
        <div className="text-center text-gray-400">No tracks found. Try playing some music on Spotify!</div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {topTracks.map((track) => (
            <div key={track.id} className="bg-gray-800 p-4 rounded-lg shadow hover:shadow-lg transition-all">
              {track.album?.images?.[0]?.url && (
                <img 
                  src={track.album.images[0].url} 
                  alt={track.name}
                  className="w-full h-48 object-cover rounded-md mb-3"
                />
              )}
              <h2 className="text-xl font-semibold">{track.name}</h2>
              <p className="text-gray-400">{track.artists.map(artist => artist.name).join(', ')}</p>
              {track.preview_url && (
                <audio controls className="mt-2 w-full">
                  <source src={track.preview_url} type="audio/mpeg" />
                </audio>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
