import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import PlayerNotch from '../components/PlayerNotch';
import SpotifyPlayer from '../components/SpotifyPlayer';

const API_URL = 'http://localhost:5001/api';

const Home = () => {
  const navigate = useNavigate();
  const [tracks, setTracks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [queue, setQueue] = useState([]);

  const refreshAccessToken = async () => {
    try {
      const refresh_token = localStorage.getItem('spotify_refresh_token');
      if (!refresh_token) {
        throw new Error('No refresh token available');
      }

      const response = await axios.get(`${API_URL}/auth/refresh`, {
        params: { refresh_token }
      });

      localStorage.setItem('spotify_access_token', response.data.access_token);
      return response.data.access_token;
    } catch (error) {
      console.error('Error refreshing token:', error);
      handleLogout();
      throw error;
    }
  };

  const fetchTracks = async (retryWithNewToken = true) => {
    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem('spotify_access_token');
      if (!token) {
        throw new Error('No access token available');
      }

      const response = await axios.get(`${API_URL}/tracks`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setTracks(response.data);
    } catch (err) {
      console.error('Error fetching tracks:', err);

      // If we get a 401 and haven't retried yet, try refreshing the token
      if (err.response?.status === 401 && retryWithNewToken) {
        try {
          const newToken = await refreshAccessToken();
          return fetchTracks(false); // Retry with new token, but don't retry again
        } catch (refreshError) {
          console.error('Error refreshing token:', refreshError);
          handleLogout();
        }
      } else {
        setError('Failed to load tracks. Please try again.');
        if (err.response?.status === 401) {
          handleLogout();
        }
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const token = localStorage.getItem('spotify_access_token');
    if (!token) {
      navigate('/');
      return;
    }
    fetchTracks();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('spotify_access_token');
    localStorage.removeItem('spotify_refresh_token');
    localStorage.removeItem('spotify_user_id');
    navigate('/');
  };

  const handlePlayPause = (track) => {
    if (currentTrack && currentTrack.id === track.id) {
      setIsPlaying(!isPlaying);
    } else {
      setCurrentTrack(track);
      setIsPlaying(true);
    }
  };

  const handleQueueTrack = (track) => {
    setQueue(prevQueue => [...prevQueue, track]);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-900 to-black text-white p-8">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold">Your Library</h1>
          <button
            onClick={handleLogout}
            className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700 transition-colors"
          >
            Logout
          </button>
        </div>

        {error && (
          <div className="bg-red-500/10 border border-red-500 text-red-500 p-4 rounded mb-4">
            {error}
          </div>
        )}

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {tracks.map(track => (
            <div
              key={track.id}
              className="bg-white/10 p-4 rounded-lg hover:bg-white/20 transition-colors"
            >
              <div className="flex items-start space-x-4">
                {track.album.images[0] && (
                  <img
                    src={track.album.images[0].url}
                    alt={track.album.name}
                    className="w-16 h-16 object-cover rounded"
                  />
                )}
                <div className="flex-1">
                  <h3 className="font-semibold truncate">{track.name}</h3>
                  <p className="text-sm text-gray-400 truncate">
                    {track.artists.map(artist => artist.name).join(', ')}
                  </p>
                  <div className="flex space-x-2 mt-2">
                    <button
                      onClick={() => handlePlayPause(track)}
                      className="px-3 py-1 bg-green-500 text-white rounded-full text-sm hover:bg-green-600 transition-colors"
                    >
                      {currentTrack?.id === track.id && isPlaying ? 'Pause' : 'Play'}
                    </button>
                    <button
                      onClick={() => handleQueueTrack(track)}
                      className="px-3 py-1 bg-purple-500 text-white rounded-full text-sm hover:bg-purple-600 transition-colors"
                    >
                      Queue
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {currentTrack && (
          <PlayerNotch
            track={currentTrack}
            isPlaying={isPlaying}
            onPlayPause={() => setIsPlaying(!isPlaying)}
            queue={queue}
          />
        )}

        {currentTrack && (
          <SpotifyPlayer
            uri={currentTrack.uri}
            isPlaying={isPlaying}
            onPlayPause={() => setIsPlaying(!isPlaying)}
          />
        )}
      </div>
    </div>
  );
};

export default Home;
