import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import PlayerNotch from '../components/PlayerNotch';
import SpotifyPlayer from '../components/SpotifyPlayer';

const Home = () => {
  const navigate = useNavigate();
  const [tracks, setTracks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [queue, setQueue] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem('spotify_access_token');
    if (!token) {
      navigate('/');
      return;
    }

    const fetchTracks = async () => {
      try {
        setLoading(true);
        setError(null);
        const response = await axios.get('http://localhost:5001/api/tracks');
        setTracks(response.data);
      } catch (err) {
        console.error('Error fetching tracks:', err);
        setError('Failed to load tracks. Please try again.');
        if (err.response?.status === 401) {
          handleLogout();
        }
      } finally {
        setLoading(false);
      }
    };

    fetchTracks();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('spotify_access_token');
    localStorage.removeItem('spotify_refresh_token');
    localStorage.removeItem('spotify_user_id');
    window.location.href = '/';
  };

  const handleTrackSelect = (track) => {
    setCurrentTrack(track);
    // Add next few tracks to queue
    const currentIndex = tracks.findIndex(t => t.id === track.id);
    const nextTracks = tracks.slice(currentIndex + 1, currentIndex + 4);
    setQueue(nextTracks);
  };

  const handleNext = () => {
    if (!currentTrack || !tracks.length) return;
    const currentIndex = tracks.findIndex(t => t.id === currentTrack.id);
    const nextTrack = tracks[currentIndex + 1];
    if (nextTrack) {
      handleTrackSelect(nextTrack);
    }
  };

  const handlePrevious = () => {
    if (!currentTrack || !tracks.length) return;
    const currentIndex = tracks.findIndex(t => t.id === currentTrack.id);
    const prevTrack = tracks[currentIndex - 1];
    if (prevTrack) {
      handleTrackSelect(prevTrack);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-500 mx-auto"></div>
          <p className="mt-4 text-gray-400">Loading your music...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="text-red-500 mb-4">{error}</div>
          <button
            onClick={() => window.location.reload()}
            className="px-4 py-2 bg-purple-600 text-white rounded hover:bg-purple-700 transition-colors"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      {/* Header */}
      <header className="p-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold">SpotCIRCLE</h1>
        <button
          onClick={handleLogout}
          className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700 transition-colors"
        >
          Logout
        </button>
      </header>

      {/* Track List */}
      <div className="max-w-4xl mx-auto p-4">
        <div className="grid gap-4">
          {tracks.map((track) => (
            <div
              key={track.id}
              onClick={() => handleTrackSelect(track)}
              className={`p-4 rounded-lg transition-all cursor-pointer ${
                currentTrack?.id === track.id
                  ? 'bg-purple-900/50 scale-[1.02]'
                  : 'bg-gray-900/50 hover:bg-gray-800/50'
              }`}
            >
              <div className="flex items-center gap-4">
                <img
                  src={track.album?.images[0]?.url}
                  alt={track.name}
                  className="w-16 h-16 rounded object-cover"
                />
                <div>
                  <h3 className="font-medium">{track.name}</h3>
                  <p className="text-gray-400 text-sm">
                    {track.artists.map(a => a.name).join(', ')}
                  </p>
                  <p className="text-gray-500 text-sm mt-1">{track.album.name}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Player */}
      {currentTrack && (
        <>
          <PlayerNotch
            track={currentTrack}
            isPlaying={isPlaying}
            onPlay={() => setIsPlaying(true)}
            onPause={() => setIsPlaying(false)}
            onNext={handleNext}
            onPrevious={handlePrevious}
            queue={queue}
          />
          <SpotifyPlayer
            trackUri={currentTrack?.uri}
            onPlayStateChange={setIsPlaying}
          />
        </>
      )}
    </div>
  );
};

export default Home;
