import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const PlayerNotch = ({ track, onPlay, onPause, onNext, onPrevious, isPlaying, queue }) => {
  const [isExpanded, setIsExpanded] = useState(false);

  if (!track) return null;

  return (
    <div className="fixed top-0 left-1/2 -translate-x-1/2 z-50">
      <motion.div
        initial={{ width: '200px', height: '40px', y: -20 }}
        animate={{
          width: isExpanded ? '400px' : '200px',
          height: isExpanded ? '200px' : '40px',
          y: isExpanded ? 0 : -20
        }}
        transition={{ type: 'spring', stiffness: 200, damping: 25 }}
        onHoverStart={() => setIsExpanded(true)}
        onHoverEnd={() => setIsExpanded(false)}
        className="bg-gray-900/95 backdrop-blur rounded-b-2xl shadow-xl overflow-hidden"
      >
        <AnimatePresence>
          {isExpanded ? (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="p-4"
            >
              <div className="flex gap-4">
                {/* Spinning vinyl with album art */}
                <motion.div 
                  className="relative w-32 h-32"
                  animate={{ rotate: 360 }}
                  transition={{ duration: 3, repeat: Infinity, ease: 'linear' }}
                >
                  <div className="absolute inset-0 rounded-full bg-black/20 backdrop-blur" />
                  <img
                    src={track.album?.images[0]?.url}
                    alt={track.name}
                    className="w-full h-full rounded-full object-cover"
                  />
                  <div className="absolute inset-1/4 rounded-full bg-gray-900/50 backdrop-blur" />
                </motion.div>

                {/* Track info and controls */}
                <div className="flex-1 flex flex-col justify-between">
                  <div>
                    <h3 className="font-bold truncate">{track.name}</h3>
                    <p className="text-gray-400 text-sm truncate">
                      {track.artists.map(a => a.name).join(', ')}
                    </p>
                  </div>

                  {/* Playback controls */}
                  <div className="flex items-center justify-center gap-4">
                    <button 
                      onClick={onPrevious}
                      className="p-2 hover:bg-gray-800 rounded-full transition-colors"
                    >
                      <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                        <path d="M8.445 14.832A1 1 0 0010 14v-2.798l5.445 3.63A1 1 0 0017 14V6a1 1 0 00-1.555-.832L10 8.798V6a1 1 0 00-1.555-.832l-6 4a1 1 0 000 1.664l6 4z" />
                      </svg>
                    </button>
                    <button 
                      onClick={isPlaying ? onPause : onPlay}
                      className="p-3 bg-green-500 rounded-full hover:bg-green-600 transition-colors"
                    >
                      {isPlaying ? (
                        <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zM7 8a1 1 0 012 0v4a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v4a1 1 0 102 0V8a1 1 0 00-1-1z" clipRule="evenodd" />
                        </svg>
                      ) : (
                        <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM9.555 7.168A1 1 0 008 8v4a1 1 0 001.555.832l3-2a1 1 0 000-1.664l-3-2z" clipRule="evenodd" />
                        </svg>
                      )}
                    </button>
                    <button 
                      onClick={onNext}
                      className="p-2 hover:bg-gray-800 rounded-full transition-colors"
                    >
                      <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                        <path d="M11.555 14.832A1 1 0 0113 14v-2.798l5.445 3.63A1 1 0 0020 14V6a1 1 0 00-1.555-.832L13 8.798V6a1 1 0 00-1.555-.832l-6 4a1 1 0 000 1.664l6 4z" />
                      </svg>
                    </button>
                  </div>

                  {/* Queue */}
                  {queue.length > 0 && (
                    <div className="mt-4">
                      <h4 className="text-sm font-semibold text-gray-400 mb-2">Next in queue</h4>
                      <div className="text-sm text-gray-300 truncate">
                        {queue[0]?.name} - {queue[0]?.artists.map(a => a.name).join(', ')}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </motion.div>
          ) : (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="h-full flex items-center justify-center"
            >
              <div className="text-sm truncate px-4">
                {track.name} - {track.artists.map(a => a.name).join(', ')}
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </motion.div>
    </div>
  );
};

export default PlayerNotch;
