import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

const API_URL = 'http://localhost:5001/api';

const TIME_RANGES = {
  'Last 4 weeks': 'short_term',
  'Last 6 months': 'medium_term',
  'All time': 'long_term'
};

const formatDuration = (minutes) => {
  if (!minutes) return '0m';
  const hrs = Math.floor(minutes / 60);
  const mins = Math.round(minutes % 60);
  return hrs > 0 ? `${hrs}h ${mins}m` : `${mins}m`;
};

const Home = () => {
  const [tracks, setTracks] = useState([]);
  const [stats, setStats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [viewMode, setViewMode] = useState('recent');
  const [timeRange, setTimeRange] = useState('medium_term');
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const handleSpotifyCallback = async (code) => {
      try {
        const response = await axios.get(`${API_URL}/auth/callback`, {
          params: { code }
        });
        const { access_token, userId } = response.data;

        localStorage.setItem('spotify_access_token', access_token);
        localStorage.setItem('spotify_user_id', userId);
        await fetchTracks(access_token, userId);

        navigate('/', { replace: true });
      } catch (err) {
        setError('Failed to authenticate with Spotify');
        setLoading(false);
      }
    };

    const params = new URLSearchParams(location.search);
    const code = params.get('code');

    if (code) {
      handleSpotifyCallback(code);
    } else {
      const savedToken = localStorage.getItem('spotify_access_token');
      const userId = localStorage.getItem('spotify_user_id');
      if (savedToken && userId) {
        fetchTracks(savedToken, userId);
      } else {
        handleLogin();
      }
    }
  }, [location, navigate]);

  useEffect(() => {
    const savedToken = localStorage.getItem('spotify_access_token');
    const userId = localStorage.getItem('spotify_user_id');
    if (savedToken && userId) {
      fetchTracks(savedToken, userId);
    }
  }, [viewMode, timeRange]);

  const handleLogin = async () => {
    try {
      const response = await axios.get(`${API_URL}/auth/login`);
      window.location.href = response.data.url;
    } catch (err) {
      setError('Failed to initialize Spotify login');
      setLoading(false);
    }
  };

  const fetchTracks = async (token, userId) => {
    try {
      setLoading(true);
      const endpoint = viewMode === 'recent' ? 'recent-tracks' : 'top-tracks';
      const params = { 
        access_token: token,
        userId: userId
      };

      if (viewMode === 'top') {
        params.time_range = timeRange;
      }

      const response = await axios.get(`${API_URL}/auth/${endpoint}`, { params });

      if (viewMode === 'recent') {
        setTracks(response.data.items.map(item => item.track));
        setStats(response.data.stats || []);
      } else {
        setTracks(response.data.items || []);
      }

      setLoading(false);
    } catch (err) {
      if (err.response?.status === 401) {
        localStorage.removeItem('spotify_access_token');
        localStorage.removeItem('spotify_user_id');
        handleLogin();
      } else {
        setError('Failed to fetch tracks');
        setLoading(false);
      }
    }
  };

  const renderTrackStats = (track, trackStats) => {
    if (!trackStats) return null;

    return (
      <div className="flex flex-col gap-1 text-sm text-gray-400 mb-2">
        {trackStats.playCount !== undefined && (
          <div className="flex justify-between">
            <span>Play count:</span>
            <span className="font-semibold">{trackStats.playCount} times</span>
          </div>
        )}
        {trackStats.totalMinutes !== undefined && (
          <div className="flex justify-between">
            <span>Total time:</span>
            <span className="font-semibold">{formatDuration(trackStats.totalMinutes)}</span>
          </div>
        )}
        {trackStats.lastPlayed && (
          <div className="flex justify-between">
            <span>Last played:</span>
            <span className="font-semibold">
              {new Date(trackStats.lastPlayed).toLocaleDateString()}
            </span>
          </div>
        )}
      </div>
    );
  };

  if (loading) return (
    <div className="flex justify-center items-center min-h-screen">
      <div className="text-xl">Loading your music profile...</div>
    </div>
  );

  if (error) return (
    <div className="flex justify-center items-center min-h-screen">
      <div className="text-xl text-red-500">{error}</div>
    </div>
  );

  return (
    <div className="container mx-auto p-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">
          {viewMode === 'recent' ? 'Recently Played' : 'Top Tracks'}
        </h1>
        <div className="flex gap-4">
          <select 
            className="bg-gray-800 text-white px-4 py-2 rounded-lg"
            value={viewMode}
            onChange={(e) => setViewMode(e.target.value)}
          >
            <option value="recent">Recently Played</option>
            <option value="top">Top Tracks</option>
          </select>

          {viewMode === 'top' && (
            <select 
              className="bg-gray-800 text-white px-4 py-2 rounded-lg"
              value={timeRange}
              onChange={(e) => setTimeRange(e.target.value)}
            >
              {Object.entries(TIME_RANGES).map(([label, value]) => (
                <option key={value} value={value}>{label}</option>
              ))}
            </select>
          )}
        </div>
      </div>

      {tracks.length === 0 ? (
        <div className="text-center text-gray-400">No tracks found. Try playing some music on Spotify!</div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {tracks.map((track) => {
            const trackStats = viewMode === 'top' ? 
              track.stats : 
              stats.find(s => s._id === track.id);

            return (
              <div key={track.id} className="bg-gray-800 p-4 rounded-lg shadow hover:shadow-lg transition-all">
                {track.album?.images?.[0]?.url && (
                  <img 
                    src={track.album.images[0].url} 
                    alt={track.name}
                    className="w-full h-48 object-cover rounded-md mb-3"
                  />
                )}
                <h2 className="text-xl font-semibold">{track.name}</h2>
                <p className="text-gray-400 mb-2">{track.artists.map(artist => artist.name).join(', ')}</p>

                {renderTrackStats(track, trackStats)}

                {track.preview_url && (
                  <audio controls className="mt-2 w-full">
                    <source src={track.preview_url} type="audio/mpeg" />
                  </audio>
                )}
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default Home;
