import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import PlayerNotch from '../components/PlayerNotch';
import SpotifyPlayer from '../components/SpotifyPlayer';

const API_URL = 'http://localhost:5001/api';

const Home = () => {
  const navigate = useNavigate();
  const [tracks, setTracks] = useState([]);
  const [topTracks, setTopTracks] = useState([]);
  const [recentTracks, setRecentTracks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [queue, setQueue] = useState([]);
  const [activeTab, setActiveTab] = useState('library');

  const refreshAccessToken = async () => {
    try {
      const refresh_token = localStorage.getItem('spotify_refresh_token');
      if (!refresh_token) {
        throw new Error('No refresh token available');
      }

      const response = await axios.get(`${API_URL}/auth/refresh`, {
        params: { refresh_token }
      });

      localStorage.setItem('spotify_access_token', response.data.access_token);
      return response.data.access_token;
    } catch (error) {
      console.error('Error refreshing token:', error);
      handleLogout();
      throw error;
    }
  };

  const fetchData = async (endpoint, retryWithNewToken = true) => {
    try {
      const token = localStorage.getItem('spotify_access_token');
      if (!token) {
        throw new Error('No access token available');
      }

      const response = await axios.get(`${API_URL}${endpoint}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      return response.data;
    } catch (err) {
      if (err.response?.status === 401 && retryWithNewToken) {
        try {
          await refreshAccessToken();
          return fetchData(endpoint, false);
        } catch (refreshError) {
          console.error('Error refreshing token:', refreshError);
          handleLogout();
        }
      }
      throw err;
    }
  };

  const fetchAllData = async () => {
    try {
      setLoading(true);
      setError(null);

      const [tracksData, topTracksData, recentTracksData] = await Promise.all([
        fetchData('/tracks'),
        fetchData('/tracks/top'),
        fetchData('/tracks/recent')
      ]);

      setTracks(tracksData);
      setTopTracks(topTracksData);
      setRecentTracks(recentTracksData);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to load music data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const token = localStorage.getItem('spotify_access_token');
    if (!token) {
      navigate('/');
      return;
    }
    fetchAllData();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('spotify_access_token');
    localStorage.removeItem('spotify_refresh_token');
    localStorage.removeItem('spotify_user_id');
    navigate('/');
  };

  const handleTrackSelect = (track) => {
    setCurrentTrack(track);
    setIsPlaying(true);
    // Add next few tracks to queue
    const currentList = activeTab === 'top' ? topTracks : activeTab === 'recent' ? recentTracks : tracks;
    const currentIndex = currentList.findIndex(t => t.id === track.id);
    const nextTracks = currentList.slice(currentIndex + 1, currentIndex + 4);
    setQueue(nextTracks);
  };

  const handlePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const handleNext = () => {
    const currentList = activeTab === 'top' ? topTracks : activeTab === 'recent' ? recentTracks : tracks;
    if (!currentTrack || !currentList.length) return;
    const currentIndex = currentList.findIndex(t => t.id === currentTrack.id);
    const nextTrack = currentList[currentIndex + 1];
    if (nextTrack) {
      handleTrackSelect(nextTrack);
    }
  };

  const handlePrevious = () => {
    const currentList = activeTab === 'top' ? topTracks : activeTab === 'recent' ? recentTracks : tracks;
    if (!currentTrack || !currentList.length) return;
    const currentIndex = currentList.findIndex(t => t.id === currentTrack.id);
    const prevTrack = currentList[currentIndex - 1];
    if (prevTrack) {
      handleTrackSelect(prevTrack);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gradient-to-b from-purple-900 to-black">
        <div className="text-center text-white">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-500 mx-auto"></div>
          <p className="mt-4">Loading your music...</p>
        </div>
      </div>
    );
  }

  const renderTracks = (trackList) => (
    <div className="grid gap-4">
      {trackList.map((track) => (
        <div
          key={track.id}
          onClick={() => handleTrackSelect(track)}
          className={`p-4 rounded-lg transition-all cursor-pointer ${
            currentTrack?.id === track.id
              ? 'bg-purple-900/50 scale-[1.02]'
              : 'bg-gray-900/50 hover:bg-gray-800/50'
          }`}
        >
          <div className="flex items-center gap-4">
            <img
              src={track.album?.images[0]?.url}
              alt={track.name}
              className="w-16 h-16 rounded object-cover"
            />
            <div>
              <h3 className="font-medium">{track.name}</h3>
              <p className="text-gray-400 text-sm">
                {track.artists.map(a => a.name).join(', ')}
              </p>
              <p className="text-gray-500 text-sm mt-1">{track.album.name}</p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-900 to-black">
      {/* Header */}
      <header className="p-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-white">SpotCIRCLE</h1>
        <button
          onClick={handleLogout}
          className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700 transition-colors"
        >
          Logout
        </button>
      </header>

      {/* Tabs */}
      <div className="max-w-4xl mx-auto px-4 mb-6">
        <div className="flex space-x-4 border-b border-gray-700">
          <button
            onClick={() => setActiveTab('library')}
            className={`px-4 py-2 text-sm font-medium transition-colors ${
              activeTab === 'library'
                ? 'text-white border-b-2 border-purple-500'
                : 'text-gray-400 hover:text-white'
            }`}
          >
            Your Library
          </button>
          <button
            onClick={() => setActiveTab('top')}
            className={`px-4 py-2 text-sm font-medium transition-colors ${
              activeTab === 'top'
                ? 'text-white border-b-2 border-purple-500'
                : 'text-gray-400 hover:text-white'
            }`}
          >
            Top Tracks
          </button>
          <button
            onClick={() => setActiveTab('recent')}
            className={`px-4 py-2 text-sm font-medium transition-colors ${
              activeTab === 'recent'
                ? 'text-white border-b-2 border-purple-500'
                : 'text-gray-400 hover:text-white'
            }`}
          >
            Recently Played
          </button>
        </div>
      </div>

      {/* Track List */}
      <div className="max-w-4xl mx-auto p-4">
        {error ? (
          <div className="text-center text-red-500 bg-red-500/10 p-4 rounded">
            {error}
            <button
              onClick={() => window.location.reload()}
              className="block mx-auto mt-4 px-4 py-2 bg-purple-600 text-white rounded hover:bg-purple-700 transition-colors"
            >
              Try Again
            </button>
          </div>
        ) : (
          renderTracks(
            activeTab === 'top'
              ? topTracks
              : activeTab === 'recent'
              ? recentTracks
              : tracks
          )
        )}
      </div>

      {/* Player */}
      {currentTrack && (
        <>
          <PlayerNotch
            track={currentTrack}
            isPlaying={isPlaying}
            onPlayPause={handlePlayPause}
            onNext={handleNext}
            onPrevious={handlePrevious}
            queue={queue}
          />
          <SpotifyPlayer
            uri={currentTrack.uri}
            isPlaying={isPlaying}
            onPlayPause={setIsPlaying}
          />
        </>
      )}
    </div>
  );
};

export default Home;
